import { Link } from 'react-router-dom';
import products from '../data';
const Products = () => {
  return (
    <>
      <section className='section'>
        <h2>products</h2>
        <div className="products">
          {products.map((product)=>{
            return (
              <article>
                <h5></h5>
              <Link to={`/products/${product.id}`}>More Info</Link>
              </article>
            )
          })}
        </div>
      </section>
    </>
  );
};

export default Products;
