import React from 'react'
import { NavLink } from 'react-router-dom'

export const StyledNavbar = () => {
  return (
    <nav className='navbar'>
        <NavLink to={'/'}
        className={({isActive})=> isActive ? 'link active':'link'}
        >Home</NavLink>
        <NavLink to={'/about'} 
        className={({isActive})=> isActive?'link active':''}
        >About</NavLink>
        <NavLink to={'/products'} 
        className={({isActive})=> isActive?'link active':''}
        >Products</NavLink>
    </nav>
  )
}
